import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from "react";
import paperImg from "./images/paper-emoji.png";
import scissorsImg from "./images/scissors-emoji.png"
import rockImg from "./images/rock-emoji.png"

const RockPaperScissors = () => {


  const [score, setScore] = useState(
    JSON.parse(localStorage.getItem("score")) || {
      wins: 0,
      losses: 0,
      ties: 0,
    }
  );

  const [isAutoPlaying, setIsAutoPlaying] = useState(false);
  const [intervalId, setIntervalId] = useState(null);
  const [result, setResult] = useState("");
  const [playerMove, setPlayerMove] = useState("");
  const [computerMove, setComputerMove] = useState("");

  useEffect(() => {
    updateScoreElement();
    document.body.addEventListener("keydown", handleKeyDown);
    return () => {
      document.body.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  useEffect(() => {
    localStorage.setItem("score", JSON.stringify(score));
    updateScoreElement();
  }, [score]);

  const handleKeyDown = (event) => {
    if (event.key === "r") {
      playGame("rock");
    } else if (event.key === "p") {
      playGame("paper");
    } else if (event.key === "s") {
      playGame("scissors");
    }
  };

  const autoPlay = () => {
    if (!isAutoPlaying) {
      setIntervalId(
        setInterval(() => {
          const playerMove = pickComputerMove();
          playGame(playerMove);
        }, 1000)
      );
      setIsAutoPlaying(true);
    } else {
      clearInterval(intervalId);
      setIsAutoPlaying(false);
    }
  };

  const playGame = (playerMove) => {
    setPlayerMove(playerMove);
    const computerMove = pickComputerMove();
    setComputerMove(computerMove);

    let result = "";

    if (playerMove === "scissors") {
      if (computerMove === "rock") {
        result = "You lose.";
      } else if (computerMove === "paper") {
        result = "You win.";
      } else if (computerMove === "scissors") {
        result = "Tie.";
      }
    } else if (playerMove === "paper") {
      if (computerMove === "rock") {
        result = "You win.";
      } else if (computerMove === "paper") {
        result = "Tie.";
      } else if (computerMove === "scissors") {
        result = "You lose.";
      }
    } else if (playerMove === "rock") {
      if (computerMove === "rock") {
        result = "Tie.";
      } else if (computerMove === "paper") {
        result = "You lose.";
      } else if (computerMove === "scissors") {
        result = "You win.";
      }
    }

    setResult(result);

    if (result === "You win.") {
      setScore((prevScore) => ({ ...prevScore, wins: prevScore.wins + 1 }));
    } else if (result === "You lose.") {
      setScore((prevScore) => ({ ...prevScore, losses: prevScore.losses + 1 }));
    } else if (result === "Tie.") {
      setScore((prevScore) => ({ ...prevScore, ties: prevScore.ties + 1 }));
    }
  };

  const updateScoreElement = () => { };

  const pickComputerMove = () => {
    const randomNumber = Math.random();

    let computerMove = "";

    if (randomNumber >= 0 && randomNumber < 1 / 3) {
      computerMove = "rock";
    } else if (randomNumber >= 1 / 3 && randomNumber < 2 / 3) {
      computerMove = "paper";
    } else if (randomNumber >= 2 / 3 && randomNumber < 1) {
      computerMove = "scissors";
    }

    return computerMove;
  };

  return (
    <>
      <button className="move-button js-rock-button" onClick={() => playGame("rock")}>
        <img src={rockImg} className="move-icon" />
      </button>

      <button className="move-button js-paper-button" onClick={() => playGame("paper")}>
        <img src="images/paper-emoji.png" className="move-icon" />
      </button>

      <button className="move-button js-scissors-button" onClick={() => playGame("scissors")}>
        <img src="images/scissors-emoji.png" className="move-icon" />
      </button>

      <p className="js-result result">{result}</p>
      <p className="js-moves">
        You
        <img src={`images/${playerMove}-emoji.png`} className="move-icon" />
        <img src={`images/${computerMove}-emoji.png`} className="move-icon" />
        Computer
      </p>
      <p className="js-score score">
        Wins: {score.wins}, Losses: {score.losses}, Ties: {score.ties}
      </p>

      <button
        onClick={() => {
          setScore({ wins: 0, losses: 0, ties: 0 });
          localStorage.removeItem("score");
          updateScoreElement();
        }}
        className="reset-score-button js-reset-score-button"
      >
        Reset Score
      </button>

      <button className="auto-play-button" onClick={() => autoPlay()}>
        Auto Play
      </button>
    </>
  );
};

export default RockPaperScissors;
